  `BORROW_ID` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键，借阅书籍ID',
  `BOOK_ID` int(11) NOT NULL COMMENT '书籍ID，外键',
  `USER_ID` int(11) NOT NULL COMMENT '用户ID，外键',
  `USR_BORROW_STATE` tinyint(1) NOT NULL DEFAULT '0' COMMENT '默认0代表可申请，1代表等待同意，2代表成功借阅，3代表已还书',
