  `RETURN_DATE_TIME` datetime DEFAULT NULL COMMENT '还书日期时间',
  PRIMARY KEY (`BORROW_ID`),
  KEY `USER_ID` (`USER_ID`),
  KEY `r_book_user_borrow_ibfk_1` (`BOOK_ID`),
  CONSTRAINT `r_book_user_borrow_ibfk_1` FOREIGN KEY (`BOOK_ID`) REFERENCES `r_user_book` (`BOOK_ID`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `r_book_user_borrow_ibfk_2` FOREIGN KEY (`USER_ID`) REFERENCES `user_inf` (`USER_ID`) ON DELETE CASCADE ON UPDATE CASCADE
