  `PRIVACY` tinyint(1) NOT NULL DEFAULT '0' COMMENT '默认0代表公开，1代表私密',
  `BORROW_STATE` tinyint(1) NOT NULL DEFAULT '0' COMMENT '默认0代表无申请，1代表待处理，2代表已借出',
  `ADD_TIME` datetime DEFAULT NULL COMMENT '加入书架时间',
  `DELETE_BOOK` tinyint(1) NOT NULL DEFAULT '0' COMMENT '默认0表示不删除，1表示从书架删除',
  PRIMARY KEY (`BOOK_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

